//
import React, { useEffect } from "react";

type Props = {
  show: boolean;
  handleClose: () => void;
  connectWallet: () => void;
};

const WalletConnectModal: React.FC<Props> = ({ show, handleClose, connectWallet }) => {
  useEffect(() => {
    if (show) {
      document.body.classList.add("modal-open");
    } else {
      document.body.classList.remove("modal-open");
    }
  }, [show]);

  if (!show) return null;

  return (
    <div
      className="modal fade show"
      style={{ display: "block", backgroundColor: "rgba(0,0,0,0.5)" }}
      tabIndex={-1}
      role="dialog"
    >
      <div className="modal-dialog modal-dialog-centered" role="document">
        <div className="modal-content border-0 rounded-3 shadow">
          <div className="modal-header">
            <h5 className="modal-title fw-bold">Connect your wallet</h5>
            <button type="button" className="btn-close" onClick={handleClose}></button>
          </div>
          <div className="modal-body text-center">
          <button
              className="btn btn-dark d-flex align-items-center justify-content-between w-100 py-2"
              onClick={connectWallet}
            >
                <img alt="Phantom icon"
                    width={28}
                    height={28}
                    className="me-2"
                    src="data:image/svg+xml;base64," 
                    style={{color: 'transparent'}} 
                />
              <span>Connect with Phantom</span>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WalletConnectModal;
